mapper.update:
update Account
set

    name = #{name}


    email = #{email}


    id = #{id}


    password = #{password}


    phoneNumber = #{phoneNumber}


    nickname = #{nickname}

where idx = #{idx}

mapper.select:
select idx, name, email, id, password,phonenumber,nickname
from account
where idx = #{idx}

select idx, name, email, id, password,phonenumber,nickname
from account
where id = #{id}

select id
from account
where id = #{id}

select count(idx)
from account
where id = #{id} and email = #{email}
